async function generate_image(params, context) {
  // Initial request to create image
  const createResponse = await fetch("https://api.bfl.ml/v1/flux-pro-1.1", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      "X-Key": context.apiKey
    },
    body: JSON.stringify({
      prompt: params.prompt,
      width: params.width || 1024,
      height: params.height || 768,
      prompt_upsampling: false,
      seed: params.seed || 42,
      safety_tolerance: 2,
      output_format: "jpeg"
    })
  });

  if (!createResponse.ok) {
    throw new Error("Failed to initiate image generation");
  }

  const { id } = await createResponse.json();

  // Poll for results
  let result;
  for (let i = 0; i < 30; i++) {
    // Maximum 30 attempts
    const checkResponse = await fetch(
      `https://api.bfl.ml/v1/get_result?id=${id}`
    );
    if (!checkResponse.ok) {
      throw new Error("Failed to check generation status");
    }

    result = await checkResponse.json();
    if (result.status !== "Pending") {
      break;
    }

    // Wait 2 seconds before next poll
    await new Promise((resolve) => setTimeout(resolve, 2000));
  }

  if (!result || result.status !== "Ready") {
    throw new Error("Something went wrong: " + JSON.stringify(result));
  }

  return `![${params.prompt}](${result.result.sample})`
}
